import React, { useState } from "react";
import "./index.css";
 

const App=()=>{
const state=useState();

let curtime=new Date().toLocaleTimeString();

const[time,settime]=useState(curtime);


const IncTime=()=>{
   curtime=new Date().toLocaleTimeString();

settime(curtime);
}
setInterval(IncTime,1000);

return(
  <>
    <h1>{time}</h1>
  </>
);

}
export default App;
