import React, { useState } from "react";

const App=()=>{
 
const[number,setNumber]=useState(0);

const Increment=()=>{
setNumber(number+1);
}
const Decrement=()=>{
if(number>0){
  setNumber(number-1);
}
}
   return(
      <>
        <h1>{number}</h1>
        <br /><br></br>
        <button onClick={Increment}>INC</button>
        <br /><br></br>
        <button onClick={Decrement}>DEC</button>
      </>
  );
}
export default App;
