import React, { useState } from "react";
import Button from '@mui/material/Button';
import AddIcon from '@mui/icons-material/Add';
const CreateNode=(props)=>{
     const[expand,setExpand]=useState(false);

    const expandIt=()=>{
        setExpand(true);
    }
    const minimiseIt=()=>{
        setExpand(false);
    }



    const[note,setNote]=useState({
        title:"",
        content:""
    });

    const InputEvent=(event)=>{
             const{name,value}=event.target;
             setNote((preData)=>{
                return{
                    ...preData,
                    [name]:value,
                }
             })
    }

    const addEvent=()=>{
     props.passNote(note);
     setNote({
        title:"",
        content:""
    });
    }
    return(
        <>
       <div className="main_notes">
         <form>
           { expand?<input type="text" 
                name="title" 
                value={note.title}
                 onChange={InputEvent} 
                 placeholder="Title"
                autoComplete="off"
            />:null}
            <textarea rows='' columns=''
            name="content"
            value={note.content} 
            onChange={InputEvent} 
            placeholder="Write a note..."
            onClick={expandIt}
            onDoubleClick={minimiseIt}>

            </textarea>
            <Button onClick={addEvent}>
             <AddIcon className="plus_sign" />
            </Button>
         </form>
       </div> 
       
        </>
    );
}
export default CreateNode;
