import React, { useState } from "react";
import './index.css';
import ToDoLists from './ToDoLists';

const App=()=>{
  const[inputList,setInputList]=useState("");
  const[Items,setItems]=useState([]);

  const itemEvent=(event)=>{
    setInputList(event.target.value);
  };
 const deleteItem=(id)=>{
  setItems((olditems)=>{
    return olditems.filter((arrElemnts,index)=>{
      return index!==id;
    })
  })
 }
  const listofitems=()=>{
      setItems((oldvalue)=>{
        return [...oldvalue,inputList];
      })
      setInputList('');
  };

    return(
     <>
      <div className="main_div">
          <div className="center_div">
             <br />
             <h1>ToDO List</h1>
             <br />
             <input type="text" placeholder="Add an item" onChange={itemEvent} 
             value={inputList}
             />
             <button onClick={listofitems}> + </button>

             <ol>
             {Items.map((itemsval,index)=>{
             return <ToDoLists 
              key={index}
              id={index}
              text={itemsval}
              onSelect={deleteItem}
               />
             })
             }
             </ol>
          </div>
      </div>
     </>
    );
}
export default App;
