import React, { useState } from "react";
import "./index.css";
 

const App=()=>{
const state=useState();
const{name,setname}=useState('Click Me');
let color="#8e44ad";
const{bg,setbg}=useState(color);



const bgchange=()=>{
  setname("ouch ðŸ˜ ");
  let newcolor="#34495e";
  setbg(newcolor);
};

  return(
    <>
      <div style={{backgroundColor : bg}}>
        <button onClick={bgchange}> {name} </button>
      </div>
    </>
  );

};
export default App;
