import React, { useState } from "react";
import "./index.css";
 

const App=()=>{
 
let color="#8e44ad";
const[bg,setBg]=useState(color);
let name='CLick Me';



const bgchange=()=>{
  let newBg="#34495e";
  setBg(newBg);
};

  return(
    <>
      <div style={{backgroundColor : bg}}>
        <button onClick={bgchange}> {name} </button>
      </div>
    </>
  );

};
export default App;
