import React, { useState } from "react";
import "./index.css";
 

const App=()=>{
const state=useState();
const curtime=new Date().toLocaleTimeString();
[curtime,settime]=useState(curtime);


const IncTime=()=>{
  curtime=new Date().toLocaleTimeString;
settime(curtime);
}

return(
  <>
    <h1>{curtime}</h1>
    <button onClick={IncTime}>Click Me</button>
  </>
);

}
export default App;
