import React, { useState } from "react";
import "./index.css";
 

const App=()=>{
const state=useState();
const{name,setname}=useState('Click Me');

let color="#9b59b6";
const{bg,setbg}=useState(color);

const change=()=>{
  setname("ouch ðŸ˜ ");
  let newcolor="#868686";
  setbg(newcolor);
}
  return(
    <>
      <div style={{backgroundColor : bg}}>
        <button onClick={change}>{name}</button>
      </div>
    </>
  );

}
export default App;
